import React, { useState } from 'react';
import axios from 'axios';
import { pdfjs } from 'pdfjs-dist/build/pdf'; // Adjusted import for pdfjs
import 'bootstrap/dist/css/bootstrap.min.css';

// Set the workerSrc property to a CDN URL
pdfjs.GlobalWorkerOptions.workerSrc = 'https://cdnjs.cloudflare.com/ajax/libs/pdf.js/2.11.338/pdf.worker.min.js';

export default function ATS() {
  const [jobDescription, setJobDescription] = useState('');
  const [resumeFile, setResumeFile] = useState(null);
  const [apiKey, setApiKey] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  const extractPdfText = async (file) => {
    try {
      const arrayBuffer = await file.arrayBuffer();
      const pdfDoc = await pdfjs.getDocument(arrayBuffer).promise;
      let text = '';

      for (let i = 1; i <= pdfDoc.numPages; i++) {
        const page = await pdfDoc.getPage(i);
        const content = await page.getTextContent();
        text += content.items.map(item => item.str).join(' ') + '\n';
      }

      return text;
    } catch (err) {
      console.error('Error extracting text from PDF:', err);
      setError('Failed to extract text from the PDF.');
      return null;
    }
  };

  const getGeminiResponse = async (inputPrompt) => {
    try {
      const response = await axios.post('https://generativeai.googleapis.com/v1/models/gemini-pro:generateText', {
        prompt: {
          text: inputPrompt
        }
      }, {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      return response.data;
    } catch (err) {
      console.error('Error fetching response from Google API:', err);
      setError('Failed to get response from the API.');
      return null;
    }
  };

  const handleSubmit = async () => {
    if (!resumeFile || !jobDescription || !apiKey) {
      setError('Please upload a resume, enter a job description, and provide an API key.');
      return;
    }

    setError(null);
    setResponse(null);

    const resumeText = await extractPdfText(resumeFile);

    if (!resumeText) {
      return;
    }

    const inputPrompt = `...`; // Your existing prompt setup

    const apiResponse = await getGeminiResponse(inputPrompt);

    if (apiResponse) {
      setResponse(apiResponse);
    }
  };

  return (
    <div className="container my-5">
      <div className="card shadow p-4">
        <h2 className="text-center mb-4">Resume Matcher ATS</h2>
        <label htmlFor="jobDescription">Job Description:</label>
        <textarea
          id="jobDescription"
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          placeholder="Paste the Job Description here"
          rows="6"
          className="form-control"
        />

        <label htmlFor="resumeFile">Upload Your Resume (PDF):</label>
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setResumeFile(e.target.files[0])}
          className="form-control"
        />

        <label htmlFor="apiKey">Google API Key:</label>
        <input
          type="password"
          value={apiKey}
          onChange={(e) => setApiKey(e.target.value)}
          placeholder="Enter your Google API Key"
          className="form-control"
        />

        <button onClick={handleSubmit} className="btn btn-primary mt-3">Submit</button>

        {error && <p style={{ color: 'red' }}>{error}</p>}

        {response && (
          <div className="response mt-3">
            <h3>Response:</h3>
            <pre>{JSON.stringify(response, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
}
